import { UPDATE_NUMBER_OF_TEAMS } from '../actions/teamsActions';

const teamsReducer = (state = { numberOfTeams: 0 }, action) => {
    switch (action.type) {
        case UPDATE_NUMBER_OF_TEAMS:
            return {...state, numberOfTeams: action.payload };
        default:
            return {...state};
    }
};
export default teamsReducer;
