import React from 'react';
import { connect } from 'react-redux';

class TeamNumberCounter extends React.Component {
    static getInitialProps({store}) {}
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="align-input-left">
                <label
                    className="control-label p-1"
                    htmlFor="disabledInput"
                >
                    Choose number of teams
                </label>
                <input
                    className="form-control player-counter"
                    type="text"
                    value={this.props.players.length}
                />
            </div>
        )
    }
}

export default connect(mapStateToProps)(TeamNumberCounter);
