import { connect } from 'react-redux';
import { removePlayer } from "../redux/actions/playerActions";

class TeamChooser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            players: []
        };
    }
    componentDidMount() {
        this.getTeams()
    }
    getTeams() {
     const selectedTeams = [] ;
     console.log(this.props.players)
        this.props.players.forEach((player, index) => {
            selectedTeams[1] = Array.isArray(selectedTeams[1]) ? selectedTeams[1] : []
            if (!selectedTeams[1].length) {
                selectedTeams[1].push(index);
            }
      })
        console.log(selectedTeams)
    }

    render() {
        return (
            <div>
                test team chooser
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    removePlayer: removePlayer,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamChooser);
