import React from 'react';
import { connect } from 'react-redux';
import { updateNumberOfTeams } from "../redux/actions/teamsActions";
import Modal from 'react-modal';

const customStyles = {
    content : {
        top                   : '50%',
        left                  : '50%',
        right                 : 'auto',
        bottom                : 'auto',
        marginRight           : '-50%',
        transform             : 'translate(-50%, -50%)'
    }
};

class TeamListModal extends React.Component {
    static getInitialProps({store}) {}
    constructor(props) {
        super(props);
        this.state = {
            isModalOpen: false
        }
    }
     openModal = () =>  {
        this.setState({ isModalOpen: true});
    }

     afterOpenModal() {
        // references are now sync'd and can be accessed.
        subtitle.style.color = '#f00';
    }

    closeModal = () => {
        this.setState({ isModalOpen: false});
    }
    render() {
        return (
            <div>
                <button
                    type="button"
                    className="btn btn-success align-input-left ml-3"
                    onClick={this.openModal}
                >
                    Choose your team
                </button>
                <div id="team-list-modal" className="modal">
                    <Modal
                        isOpen={this.state.isModalOpen}
                        onRequestClose={this.closeModal}
                        style={customStyles}
                        contentLabel="Example Modal"
                    >
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Modal title</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                <p>Modal body text goes here.</p>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-primary">Save changes</button>
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={this.closeModal}
                                    data-dismiss="modal">Close
                                </button>
                            </div>
                        </div>
                    </div>
                    </Modal>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    numberOfTeams: state.teams.numberOfTeams
});
const mapDispatchToProps = {
    updateNumberOfTeams: updateNumberOfTeams,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamListModal);
