import { connect } from 'react-redux';
import { removePlayer } from "../redux/actions/playerActions";

class TeamChooser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            players: []
        };
    }

    getTeams() {
     const selectedTeams = {} ;
     let teamNumber = 1;
      this.props.players.forEach((player, index) => {
          selectedTeams[teamNumber] = [...selectedTeam[teamNumber], index]
          teamNumber = teamNumber === 1 ? 1 : 0
      })
        console.log(selectedTeams)
    }

    render() {
        return (
            <div>
                <div className="d-flex justify-content-center">
                    <div className="list-group">
                        {
                            this.props.players.map((player, index) =>
                                <a key={index} className="list-group-item list-group-item-action player-item">
                                    {player}
                                    <button
                                        onClick={() => this.props.removePlayer(index)}
                                        type="button" className="close"
                                        aria-label="Close"
                                    >
                                        <span className="text-danger" aria-hidden="true">&times;</span>
                                    </button>
                                </a>
                            )
                        }
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    removePlayer: removePlayer,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamChooser);
