import React from 'react';
import { connect } from 'react-redux';

class PlayerCounter extends React.Component {
    static getInitialProps({store}) {}
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div className="align-input-left">
                <label
                    className="control-label p-1"
                    htmlFor="disabledInput"
                >
                    Number of players:
                </label>
                <input
                    className="form-control player-counter"
                    type="text"
                    disabled
                    value={this.props.players.length}
                />
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

export default connect(mapStateToProps)(PlayerCounter);
