import React from 'react';
import { connect } from 'react-redux';
import { addPlayer } from '../redux/actions/playerActions';

class AddPlayer extends React.Component {
    static getInitialProps({store}) {}
    constructor(props) {
        super(props);
        this.state = {
            playersName: ''
        };
    }
    setPlayersName(value) {
        this.setState({ playersName: value})
    }
    addPlayer(playersName) {
        if (playersName) {
            this.props.addPlayer(playersName)
            console.log(playersName)
            this.setState({ playersName: ''}, () => {
                console.log(this.state.playersName)
            })
        }
    }
    render() {
        return (
            <div className="d-flex justify-content-center">
                <div className="row">
                    <div className="form-group">
                        <div className="input-group mb-3">
                            <input
                                type="text"
                                className="form-control"
                                aria-label="Amount (to the nearest dollar)"
                                defaultValue={this.state.playersName}
                                value=""
                                onChange={
                                    (event) =>  this.setPlayersName(event.target.value)}
                            />
                            <div className="input-group-append">
                                <button
                                    type="button"
                                    className="btn btn-primary"
                                    onClick={() => this.addPlayer(this.state.playersName)}
                                >
                                    Add player
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    addPlayer: addPlayer,
};

export default connect(mapStateToProps, mapDispatchToProps)(AddPlayer);
