import { connect } from 'react-redux';
import { removePlayer } from "../redux/actions/playerActions";

class TeamChooser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            players: []
        };
    }
    componentDidMount() {
        this.getTeams()
    }
    getTeams() {
     const selectedTeams = [] ;
     console.log(this.props.players)
     let teamNumber = teamNumber === 1 ? 2 : 1
        this.props.players.forEach((player, index) => {
          console.log('index '+index)
          console.log('teamNumber'+teamNumber)
          if(!Array.isArray(selectedTeams[teamNumber])) {
              console.log('not an array')
              selectedTeams[teamNumber] = []
          }
          selectedTeams[teamNumber] = selectedTeams[teamNumber].push(index)
          console.log(selectedTeams[teamNumber])
          console.log('team number'+ teamNumber)

      })
        console.log(selectedTeams)
    }

    render() {
        return (
            <div>
                test team chooser
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    removePlayer: removePlayer,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamChooser);
