import { connect } from 'react-redux';
import { removePlayer } from "../redux/actions/playerActions";

class TeamChooser extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            players: []
        };
    }
    componentDidMount() {
        this.getTeams()
    }
    getTeams() {
     const selectedTeams = [] ;
     let teamNumber = 1;
     console.log(this.props.players)
      this.props.players.forEach((player, index) => {
          console.log(index)
          selectedTeams[teamNumber] = selectedTeams[teamNumber] ? selectedTeams[teamNumber].push(index): [index]
          teamNumber = teamNumber === 1 ? 2 : 1
      })
        console.log(selectedTeams[1])
    }

    render() {
        return (
            <div>
                test team chooser
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    removePlayer: removePlayer,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamChooser);
