    constructor(props) {
        super(props);
        this.state = {
            players: []
        };
    }
    componentDidMount() {
        this.getTeams()
    }
    getTeams() {
     const selectedTeams = [[]] ;
     let teamNumber = 0;
     console.log(this.props.players)
      this.props.players.forEach((player, index) => {
          console.log(index)
          console.log(selectedTeams[teamNumber])
          //selectedTeams[teamNumber] = selectedTeams[teamNumber] ? selectedTeams[teamNumber].push(index): [index]
          console.log(selectedTeams[teamNumber])
          console.log('team number'+ teamNumber)

          teamNumber = teamNumber === 0 ? 1 : 0
      })
        console.log(selectedTeams)
    }

    render() {
        return (
            <div>
                test team chooser
            </div>
        )
    }
}
const mapStateToProps = state => ({
    players: state.player.players
});

const mapDispatchToProps = {
    removePlayer: removePlayer,
};
export default connect(mapStateToProps, mapDispatchToProps)(TeamChooser);
